mapper.update:
update questionboard 
set title = #{title}
	,question_contents = #{question_contents}
	,image_name = #{image_name} 
	where question_num = #{question_num}

mapper.insert:
insert into questionboard 
( question_num
,title
,userid
,question_contents
,wirteday
,image_name 
) 
values(
seq_question_num.NEXTVAL
,#{title}
,#{userid}
,#{question_contents}
,sysdate
,#{image_name}
)
